import React, { Component } from 'react';
import { storage, database } from '../../configFirebase';
import './style.css';
class UploadFiles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filesAudio: null,
            filesImage: null,
            ArtistName: '',
            AudioName: '',
            progressBar: 0,
            urlAudio: null,
            urlImage: null
        }
    }
    handleChangeFiles = (e) => {
        if (e.target.name === "filesAudio" || e.target.name === "filesImage") {
            this.setState({
                [e.target.name]: e.target.files[0]
            })
        }
        else {
            this.setState({
                [e.target.name]: e.target.value
            })
        }
    }
    handleSaveAudio = () => {
        const uploadTask = storage.ref(`Audios/${this.state.filesAudio.name}`).put(this.state.filesAudio);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Audios`)
                    .child(this.state.filesAudio.name)
                    .getDownloadURL()
                    .then(urlAudio => {
                        this.setState = {
                            urlAudio
                        }
                    })
            }
        )
    }
    handleSaveImage = () => {
        const uploadTask = storage.ref(`Images/${this.state.filesImage.name}`).put(this.state.filesImage);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Images`)
                    .child(this.state.filesImage.name)
                    .getDownloadURL()
                    .then(urlImage => {
                        this.setState = {
                            urlImage
                        }
                    })
            }
        )
    }
    onUploadDatabase = () => {
        const databaseRef = database.ref('Audios');
        databaseRef.push({

        })
    }
    render() {
        const { filesAudio, urlAudio, ArtistName, AudioName } = this.state;
        return (
            <div className="form-upload">
                <div className="wrapper-audio">
                    <p className="files-title">Upload Files</p>
                    <input
                        type="file"
                        name="filesAudio"
                        className="files-input"
                        onChange={this.handleChangeFiles} />
                    <button
                        onClick={this.handleSaveAudio}
                    ><svg width="2em" height="2em" viewBox="0 0 16 16" class="bi bi-cloud-arrow-down" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                            <path fill-rule="evenodd" d="M4.406 3.342A5.53 5.53 0 0 1 8 2c2.69 0 4.923 2 5.166 4.579C14.758 6.804 16 8.137 16 9.773 16 11.569 14.502 13 12.687 13H3.781C1.708 13 0 11.366 0 9.318c0-1.763 1.266-3.223 2.942-3.593.143-.863.698-1.723 1.464-2.383zm.653.757c-.757.653-1.153 1.44-1.153 2.056v.448l-.445.049C2.064 6.805 1 7.952 1 9.318 1 10.785 2.23 12 3.781 12h8.906C13.98 12 15 10.988 15 9.773c0-1.216-1.02-2.228-2.313-2.228h-.5v-.5C12.188 4.825 10.328 3 8 3a4.53 4.53 0 0 0-2.941 1.1z" />
                            <path fill-rule="evenodd" d="M7.646 10.854a.5.5 0 0 0 .708 0l2-2a.5.5 0 0 0-.708-.708L8.5 9.293V5.5a.5.5 0 0 0-1 0v3.793L6.354 8.146a.5.5 0 1 0-.708.708l2 2z" />
                        </svg></button>
                </div>
                <div className="wrapper-image">
                    <input
                        type="file"
                        name="filesImage"
                        className="files-input"
                        onChange={this.handleChangeFiles} />
                    <button
                        onClick={this.handleSaveImage}
                    ><svg width="2em" height="2em" viewBox="0 0 16 16" class="bi bi-cloud-arrow-down" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                            <path fill-rule="evenodd" d="M4.406 3.342A5.53 5.53 0 0 1 8 2c2.69 0 4.923 2 5.166 4.579C14.758 6.804 16 8.137 16 9.773 16 11.569 14.502 13 12.687 13H3.781C1.708 13 0 11.366 0 9.318c0-1.763 1.266-3.223 2.942-3.593.143-.863.698-1.723 1.464-2.383zm.653.757c-.757.653-1.153 1.44-1.153 2.056v.448l-.445.049C2.064 6.805 1 7.952 1 9.318 1 10.785 2.23 12 3.781 12h8.906C13.98 12 15 10.988 15 9.773c0-1.216-1.02-2.228-2.313-2.228h-.5v-.5C12.188 4.825 10.328 3 8 3a4.53 4.53 0 0 0-2.941 1.1z" />
                            <path fill-rule="evenodd" d="M7.646 10.854a.5.5 0 0 0 .708 0l2-2a.5.5 0 0 0-.708-.708L8.5 9.293V5.5a.5.5 0 0 0-1 0v3.793L6.354 8.146a.5.5 0 1 0-.708.708l2 2z" />
                        </svg></button>
                </div>
                <div className="wrapper-upload">
                    <input
                        placeholder="Artist Name"
                        onChange={this.handleChangeFiles}
                        value={this.state.ArtistName}
                        name="ArtistName"
                        type="text" />
                    <input
                        placeholder="Audio Name"
                        value={this.state.AudioName}
                        onChange={this.handleChangeFiles}
                        name="AudioName"
                        type="text" />
                    <button
                        onClick={this.onUploadDatabase}
                    >Upload</button>
                </div>
            </div>
        );
    }
}

export default UploadFiles;
