import React, { useState } from 'react';
import { storage, database } from '../../configFirebase';
import { FaCloudUploadAlt } from 'react-icons/fa';
import './style.css';
import { IconContext } from 'react-icons/lib';


function Uploadfiles() {
    const [fileAudio, setfileAudio] = useState(null);
    const [fileImage, setfileImage] = useState(null);
    const [urlAudio, setUrlAudio] = useState(null);
    const [urlImage, setUrlImage] = useState(null);
    const [progessAudio, setProgessAudio] = useState(null);
    const [progessImage, setProgessImage] = useState(null);
    const [infoAudio, setInfoAudio] = useState({
        artistAudio: '',
        nameAudio: ''
    })
    const handleChangeFiles = (e) => {
        if (e.target.name === "fileAudio") {
            setfileAudio(e.target.files[0])
        }
        if (e.target.name === "fileImage") {
            setfileImage(e.target.files[0])
        }
    }
    const handleChangeText = (e) => {
        if (e.target.value) {
            setInfoAudio({
                [e.target.name]: e.target.value
            })
        }
    }
    const handleSaveAudio = () => {
        if (fileAudio) {
            const uploadTask = storage.ref(`Audios/${fileAudio.name}`).put(fileAudio);
            uploadTask.on(
                "state_changed",
                (snapshot) => {
                    const progressBarAudio = Math.round(
                        (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                    );
                    setProgessAudio(progressBarAudio);
                },
                error => {
                    console.log('err', error);
                },
                () => {
                    storage
                        .ref(`Audios`)
                        .child(fileAudio.name)
                        .getDownloadURL()
                        .then(urlAudio => {
                            setUrlAudio(urlAudio);
                        })
                }
            )
        }
        else {
            console.log('err')
        }
    }
    const handleSaveImage = () => {
        if (fileImage) {
            const uploadTask = storage.ref(`Images/${fileImage.name}`).put(fileImage);
            uploadTask.on(
                "state_changed",
                (snapshot) => {
                    const progressBarImage = Math.round(
                        (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                    );
                    setProgessImage(progressBarImage);
                },
                error => {
                    console.log('err', error);
                },
                () => {
                    storage
                        .ref(`Images`)
                        .child(fileImage.name)
                        .getDownloadURL()
                        .then(urlImage => {
                            setUrlImage(urlImage);
                        })
                }
            )
        }
        else {
            console.log('err')
        }
    }
    const onUploadDatabase = () => {
        const databaseRef = database.ref('Audios');
        databaseRef.push({

        })
    }
    console.log(infoAudio)
    return (
        <div className="form-upload">
            <p className="files-title">Upload Files</p>
            <div className="wrapper-audio">
                <span className="files-name">Audio</span>
                <input
                    type="file"
                    name="fileAudio"
                    accept=".mp3"
                    className="files-input"
                    onChange={handleChangeFiles} />
                <button
                    className="files-button"
                    onClick={handleSaveAudio}
                >
                    <IconContext.Provider value={{ size: 20, color: '#ffffff' }}>
                        <FaCloudUploadAlt />
                    </IconContext.Provider>
                </button>
                <div className="progress" style={{ height: '8px', }}>
                    <div
                        className="progress-bar progress-bar-striped "
                        role="progressbar"
                        aria-valuemin="0"
                        aria-valuemax="100"
                        style={{ width: `${progessAudio}%` }}>
                    </div>
                </div>
            </div>
            <div className="wrapper-image">
                <span className="files-name">Image</span>
                <input
                    accept=".jpg"
                    type="file"
                    name="fileImage"
                    className="files-input"
                    onChange={handleChangeFiles} />
                <button
                    className="files-button"
                    onClick={handleSaveImage}
                >
                    <IconContext.Provider value={{ size: 20, color: '#ffffff' }}>
                        <FaCloudUploadAlt />
                    </IconContext.Provider>
                </button>
                <div className="progress" style={{ height: '8px', Backgroundcolor: '#56184d' }}>
                    <div
                        style={{ backgroundColor: '#56184d' }}
                        className="progress-bar progress-bar-striped"
                        role="progressbar"
                        aria-valuemin="0"
                        aria-valuemax="100"
                        style={{ width: `${progessImage}%` }}>
                    </div>
                </div>
            </div>
            <div className="wrapper-upload">
                <input
                    placeholder="Artist Name"
                    name="artistName"
                    className="files-text"
                    type="text" />
                    onChange = {handleChangeText}
                <input
                    placeholder="Audio Name"
                    onChange={handleChangeText}
                    name="audioName"
                    className="files-text"
                    type="text" />
                <button
                    className="files-button__upload"
                    onClick={onUploadDatabase}
                >Lưu lại</button>
            </div>
        </div>
    );
}

export default Uploadfiles;
