import React, { PureComponent } from 'react';
import { storage, database } from '../../configFirebase';
import { FaCloudUploadAlt } from 'react-icons/fa';
import './style.css';
import { IconContext } from 'react-icons/lib';
export default class UploadFiles extends PureComponent {
    constructor(props) {
        super(props);
        this.state = {
            fileAudio: null,
            fileImage: null,
            ArtistName: '',
            AudioName: '',
            progressBarAudio: 0,
            progressBarImage: 0,
            urlAudio: null,
            urlImage: null
        }
    }
    selectAudio = (e) => {
        if (e.target.name === "fileAudio" || e.target.name === "fileImage") {
            this.setState({
                [e.target.name]: e.target.files[0],
            });
        } else {
            this.setState({
                [e.target.name]: e.target.value,
            });
        }
    };
    handleChangeFiles = (e) => {
        if (e.target.name === "fileAudio" || e.target.name === "fileImage") {
            this.setState({
                [e.target.name]: e.target.files[0],
            });
        } else {
            this.setState({
                [e.target.name]: e.target.value,
            });
        }
    }
    handleSaveAudio = () => {
        const { fileAudio } = this.state;
        if (fileAudio) {
            const uploadTask = storage.ref(`Audios/${fileAudio.name}`).put(fileAudio);
            uploadTask.on(
                "state_changed",
                (snapshot) => {
                    const progressBarAudio = Math.round(
                        (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                    );
                    this.setState({ progressBarAudio });
                },
                error => {
                    console.log('err', error);
                },
                () => {
                    storage
                        .ref(`Audios`)
                        .child(fileAudio.name)
                        .getDownloadURL()
                        .then(urlAudio => {
                            this.setState = {
                                urlAudio
                            }
                        })
                }
            )
        }
        else {
            console.log('err')
        }
    }
    handleSaveImage = () => {
        const { fileImage } = this.state;
        if (fileImage) {
            const uploadTask = storage.ref(`Images/${fileImage.name}`).put(fileImage);
            uploadTask.on(
                "state_changed",
                (snapshot) => {
                    const progressBarImage = Math.round(
                        (snapshot.bytesTransferred / snapshot.totalBytes) * 100
                    );
                    this.setState({ progressBarImage });
                },
                error => {
                    console.log('err', error);
                },
                () => {
                    storage
                        .ref(`Images`)
                        .child(fileImage.name)
                        .getDownloadURL()
                        .then(urlImage => {
                            this.setState = {
                                urlImage
                            }
                        })
                }
            )
        }
        else {
            console.log('err')
        }
    }
    onUploadDatabase = () => {
        const databaseRef = database.ref('Audios');
        databaseRef.push({

        })
    }
    render() {
        const { progressBarAudio, progressBarImage, fileImage, fileAudio } = this.state;
        console.log(progressBarAudio, progressBarImage)
        console.log(fileAudio)
        console.log(fileImage)
        return (
            <div className="form-upload">
                <p className="files-title">Upload Files</p>
                <div className="wrapper-audio">
                    <span className="files-name">Audio</span>
                    <input
                        type="file"
                        name="fileAudio"
                        accept=".mp3"
                        className="files-input"
                        onChange={this.selectAudio} />
                    <button
                        className="files-button"
                        onClick={this.handleSaveAudio}
                    >
                        <IconContext.Provider value={{ size: 20, color: '#ffffff' }}>
                            <FaCloudUploadAlt />
                        </IconContext.Provider>
                    </button>
                    <div className="progress" style={{ height: '8px', }}>
                        <div
                            className="progress-bar progress-bar-striped "
                            role="progressbar"
                            aria-valuemin="0"
                            aria-valuemax="100"
                            style={{ width: `${this.state.progressBarAudio}%` }}>
                        </div>
                    </div>
                </div>
                <div className="wrapper-image">
                    <span className="files-name">Image</span>
                    <input
                        accept=".jpg"
                        type="file"
                        name="fileImage"
                        className="files-input"
                        onChange={this.selectAudio} />
                    <button
                        className="files-button"
                        onClick={this.handleSaveImage}
                    >
                        <IconContext.Provider value={{ size: 20, color: '#ffffff' }}>
                            <FaCloudUploadAlt />
                        </IconContext.Provider>
                    </button>
                    <div className="progress" style={{ height: '8px', Backgroundcolor: '#56184d' }}>
                        <div
                            style={{ backgroundColor: '#56184d' }}
                            className="progress-bar progress-bar-striped"
                            role="progressbar"
                            aria-valuemin="0"
                            aria-valuemax="100"
                            style={{ width: `${this.state.progressBarImage}%` }}>
                        </div>
                    </div>
                </div>
                <div className="wrapper-upload">
                    <input
                        placeholder="Artist Name"
                        onChange={this.handleChangeFiles}
                        value={this.state.ArtistName}
                        name="ArtistName"
                        className="files-text"
                        type="text" />
                    <input
                        placeholder="Audio Name"
                        value={this.state.AudioName}
                        onChange={this.handleChangeFiles}
                        name="AudioName"
                        className="files-text"
                        type="text" />
                    <button
                        className="files-button__upload"
                        onClick={this.onUploadDatabase}
                    >Lưu lại</button>
                </div>
            </div>
        );
    }
}
