import logo from './logo.svg';
import './App.css';
import firebase, { storage, database } from './config';
import { useState } from 'react';

function App() {
    const [files, setFiless] = useState(null);
    const [srcAudio, setSrcAudio] = useState(null);
    const handleChangeFiles = (e) => {
        if (e.target.files[0]) {
            setFiless(e.target.files[0]);
        }
    }
    const onSave = () => {
        const uploadTask = storage.ref(`Audios/${files.name}`).put(files);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Audios`)
                    .child(files.name)
                    .getDownloadURL()
                    .then(url => {
                        setSrcAudio(url);
                    })
            }
        )
    }
    const onUploadDatabase = () => {
        const databaseRef = database.ref('Audios');
        databaseRef.push({
            src: srcAudio
        })
    }
    return (
        <div className="App">
            <input
                type="file"
                id="test"
                onChange={handleChangeFiles} />
            <button
                onClick={onSave}
            >Save</button>
            <button
                onClick={onUploadDatabase}
            >Upload</button>
        </div>
    );
}

export default App;
