import React, { Component } from 'react';
import { storage, database } from '../../configFirebase';
import { FaCloudUploadAlt } from 'react-icons/fa';
import './style.css';
import { IconContext } from 'react-icons/lib';
class UploadFiles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filesAudio: null,
            filesImage: null,
            ArtistName: '',
            AudioName: '',
            progressBarAudio: 0,
            progressBarImage: 0,
            urlAudio: null,
            urlImage: null
        }
    }
    handleChangeFiles = (e) => {
        if (e.target.name === "filesAudio" || e.target.name === "filesImage") {
            this.setState({
                [e.target.name]: e.target.files[0]
            })
        }
        else {
            this.setState({
                [e.target.name]: e.target.value
            })
        }
    }
    handleSaveAudio = () => {
        const uploadTask = storage.ref(`Audios/${this.state.filesAudio.name}`).put(this.state.filesAudio);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Audios`)
                    .child(this.state.filesAudio.name)
                    .getDownloadURL()
                    .then(urlAudio => {
                        this.setState = {
                            urlAudio
                        }
                    })
            }
        )
    }
    handleSaveImage = () => {
        const uploadTask = storage.ref(`Images/${this.state.filesImage.name}`).put(this.state.filesImage);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Images`)
                    .child(this.state.filesImage.name)
                    .getDownloadURL()
                    .then(urlImage => {
                        this.setState = {
                            urlImage
                        }
                    })
            }
        )
    }
    onUploadDatabase = () => {
        const databaseRef = database.ref('Audios');
        databaseRef.push({

        })
    }
    render() {
        const { filesAudio, urlAudio, ArtistName, AudioName } = this.state;
        return (
            <div className="form-upload">
                <p className="files-title">Upload Files</p>
                <div className="wrapper-audio">
                    <span className="files-name">Audio</span>
                    <input
                        type="file"
                        name="filesAudio"
                        className="files-input"
                        onChange={this.handleChangeFiles} />
                    <button
                        className="files-button"
                        onClick={this.handleSaveAudio}
                    >
                        <IconContext.Provider value={{ size: 20, color: '#ffffff' }}>
                            <FaCloudUploadAlt />
                        </IconContext.Provider>
                    </button>
                    <div class="progress" style={{ height: '8px' }}>
                        <div class="progress-bar progress-bar-striped active" role="progressbar"
                            aria-valuenow="50" aria-valuemin="0" aria-valuemax="100" style={{ width: '40%' }}>
                            40%
                        </div>
                    </div>
                </div>
                <div className="wrapper-image">
                    <span className="files-name">Image</span>
                    <input
                        type="file"
                        name="filesImage"
                        className="files-input"
                        onChange={this.handleChangeFiles} />
                    <button
                        className="files-button"
                        onClick={this.handleSaveImage}
                    >
                        <IconContext.Provider value={{ size: 20, color: '#ffffff' }}>
                            <FaCloudUploadAlt />
                        </IconContext.Provider>
                    </button>
                </div>
                <div className="wrapper-upload">
                    <input
                        placeholder="Artist Name"
                        onChange={this.handleChangeFiles}
                        value={this.state.ArtistName}
                        name="ArtistName"
                        type="text" />
                    <input
                        placeholder="Audio Name"
                        value={this.state.AudioName}
                        onChange={this.handleChangeFiles}
                        name="AudioName"
                        type="text" />
                    <button
                        onClick={this.onUploadDatabase}
                    >Upload</button>
                </div>
            </div>
        );
    }
}

export default UploadFiles;
