import React, { Component } from 'react';
import { storage, database } from '../../configFirebase';
import './style.css';
class UploadFiles extends Component {
    constructor(props) {
        super(props);
        this.state = {
            filesAudio: null,
            filesImage: null,
            singerName: '',
            progressBar: 0,
            urlAudio: null,
            urlImage: null
        }
    }
    handleChangeFiles = (e) => {
        if (e.target.name === "filesAudio" || e.target.name === "filesImage") {
            this.setState({
                [e.target.name]: e.target.files[0]
            })
        }
    }
    handleSaveAudio = () => {
        const uploadTask = storage.ref(`Audios/${this.state.filesAudio.name}`).put(this.state.filesAudio);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Audios`)
                    .child(this.state.filesAudio.name)
                    .getDownloadURL()
                    .then(urlAudio => {
                        this.setState = {
                            urlAudio
                        }
                    })
            }
        )
    }
    handleSaveImage = () => {
        const uploadTask = storage.ref(`Images/${this.state.filesImage.name}`).put(this.state.filesImage);
        uploadTask.on(
            "state_changed",
            snapshot => { },
            error => {
                console.log('err', error);
            },
            () => {
                storage
                    .ref(`Images`)
                    .child(this.state.filesImage.name)
                    .getDownloadURL()
                    .then(urlImage => {
                        this.setState = {
                            urlImage
                        }
                    })
            }
        )
    }
    onUploadDatabase = () => {
        const databaseRef = database.ref('Audios');
        databaseRef.push({

        })
    }
    render() {
        const { filesAudio, urlAudio } = this.state;
        return (
            <div>
                <input
                    type="file"
                    id="test"
                    name="filesAudio"
                    onChange={this.handleChangeFiles} />
                <input
                    type="file"
                    id="test"
                    name="filesImage"
                    onChange={this.handleChangeFiles} />
                <button
                    onClick={this.handleSaveAudio}
                >Save</button>
                <button
                    onClick={this.handleSaveImage}
                >Save</button>
                <button
                    onClick={this.onUploadDatabase}
                >Upload</button>
            </div>
        );
    }
}

export default UploadFiles;
