import logo from './logo.svg';
import './App.css';
import firebase, { storage } from './config';
import { useState } from 'react';

function App() {
  const [files, setFiless] = useState(null);
  const handleChangeFiles = (e) => {
    if (e.target.files[0]) {
      setFiless(e.target.files[0]);
    }
  }
  const onSave = () => {
    const uploadTask = storage.ref(`mp3/${files.name}`).put(files);
    console.log(uploadTask);
    uploadTask.on(
      'state_changed',
      snapshot => { },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref('mp3')
          .child(files.name)
          .getDownloadURL()
          .then(url => {
            console.log(url);
          })
      }
    )
  }
  console.log(files);
  return (
    <div className="App">
      <input
        type="file"
        id="test"
        onChange={handleChangeFiles} />
      <button
        onClick={onSave}
      >Save</button>
    </div>
  );
}

export default App;
