import logo from './logo.svg';
import './App.css';
import firebase, { storage } from './config';
import { useState } from 'react';

function App() {
  const [filess, setFiless] = useState(null);
  const handleChangeFiles = (e) => {
    if (e.target.files[0]) {
      setFiless(e.target.files[0]);
    }
  }
  const onSave = () => {
    const uploadTask = storage.ref(`mp3/${filess.name}`).put(filess);
    console.log(uploadTask);
  }
  console.log(filess);
  return (
    <div className="App">
      <input
        type="file"
        id="test"
        onChange={handleChangeFiles} />
      <button
        onClick={onSave}
      >Save</button>
    </div>
  );
}

export default App;
