import { useEffect, useRef, useState } from 'react';
import './App.css';
import UploadFiles from './components/UploadFiles';
import AudioItems from './components/AudioItems';
import TimeRangeSlider from 'react-time-range-slider';
import { database } from './configFirebase';
import {
    AiOutlinePauseCircle,
    AiOutlinePlayCircle
} from 'react-icons/ai';
import {
    BiSkipNextCircle,
    BiSkipPreviousCircle
} from 'react-icons/bi'
import { IconContext } from 'react-icons/lib';

const databaseRef = database.ref('Audios');
function App() {
    const audioRef = useRef();
    const [playAndPauseAudio, setplayAndPauseAudio] = useState(true);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [audios, setAudios] = useState([
        {
            src: '',
            image: '',
            title: 'Vui lòng chờ',
            artist: '',
            id: 0
        }
    ])
    useEffect(() => {
        async function fetchData() {
            try {
                databaseRef.once('value').then(res => {
                    const data = [];
                    const keys = Object.keys(res.val());
                    const values = Object.values(res.val());
                    keys.map((key, index) => {
                        values[index].id = key;
                        data.push(values[index]);
                        return data;
                    })
                    setAudios(data);
                })
            }
            catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, [])
    const onClickAudio = () => {
        if (playAndPauseAudio) {
            audioRef.current.play();
        } else {
            audioRef.current.pause();
        }
        setplayAndPauseAudio(!playAndPauseAudio);
    }
    const handleOnLoadedData = () => {
        setDuration(audioRef.current.duration);
    }
    const renderTime = () => {
        let html = null;
        let cTimeMinute = Math.floor(currentTime / 60);
        let cTimeSecond = Math.floor(currentTime % 60);
        let dTimeMinute = Math.floor(duration / 60);
        let dTimeSecond = Math.floor(duration % 60);
        let cTime = `${cTimeMinute} : ${cTimeSecond < 10 ? `0${cTimeSecond}` : `${cTimeSecond}`
            }`;
        let dTime = `${dTimeMinute} : ${dTimeSecond < 10 ? `0${dTimeSecond}` : `${dTimeSecond}`
            }`;
        html = `${cTime} / ${dTime}`;
        return html;
    }
    console.log(duration);
    return (
        <div className="container-w">
            <div className="wrapper-audio">
                <img className="audio-img" src={audios[0].image} />
                <p className="audio-name">{audios[0].name}</p>
                <p className="audio-artist">{audios[0].artist}</p>
                <audio
                    ref={audioRef}
                    src={audios[0].src}
                    onLoadedData={handleOnLoadedData}
                ></audio>
                <p className="audio-time">
                    {renderTime()}
                </p>
                <div className="audio-controls">
                    <button className="audio-button" onClick={onClickAudio}>
                        <IconContext.Provider value={{ size: 40 }}>
                            <BiSkipPreviousCircle />
                        </IconContext.Provider>
                    </button>
                    <button className="audio-button" onClick={onClickAudio}>
                        <IconContext.Provider value={{ size: 40 }}>
                            {playAndPauseAudio ? <AiOutlinePlayCircle /> : <AiOutlinePauseCircle />}
                        </IconContext.Provider>
                    </button>
                    <button className="audio-button" onClick={onClickAudio}>
                        <IconContext.Provider value={{ size: 40 }}>
                            <BiSkipNextCircle />
                        </IconContext.Provider>
                    </button>
                </div>
                <TimeRangeSlider
                    x={currentTime}
                    xMax={duration}
                    styles={{
                        track: {
                            backgroundColor: "#e3e3e3",
                            height: "2px",
                        },
                        active: {
                            backgroundColor: "#333",
                            height: "2px",
                        },
                        thumb: {
                            marginTop: "-3px",
                            width: "8px",
                            height: "8px",
                            backgroundColor: "#333",
                            borderRadius: 0,
                        },
                    }}
                />
            </div>
            <div className="wrapper-song">
                <p className="title-song">List Audio</p>
                <AudioItems data={audios} />
            </div>
        </div>

    );
}

export default App;
