import { useEffect, useRef, useState } from 'react';
import './App.css';
import UploadFiles from './components/UploadFiles';
import AudioItems from './components/AudioItems';
import TimeSlider from 'react-input-slider';
import { database } from './configFirebase';
import {
    AiOutlinePauseCircle,
    AiOutlinePlayCircle,
    AiOutlineCloseCircle
} from 'react-icons/ai';
import {
    BiSkipNextCircle,
    BiSkipPreviousCircle
} from 'react-icons/bi'
import { IconContext } from 'react-icons/lib';

const databaseRef = database.ref('Audios');

const findIndex = (id, array) => {
    let result = -1;
    array.forEach((ob, index) => {
        if (ob.id === id) {
            result = index;
        }
    });
    return result;
}

function App() {
    const audioRef = useRef();
    const [playAndPauseAudio, setplayAndPauseAudio] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);
    const [index, setIndex] = useState(0);
    const [activeID, setActiveID] = useState(0);
    const [closeFormUpload, setCloseFormUpLoad] = useState(false);
    const [audios, setAudios] = useState([
        {
            src: '',
            image: '',
            title: 'Vui lòng chờ',
            artist: '',
            id: 0
        }
    ])
    useEffect(() => {
        async function fetchData() {
            try {
                databaseRef.once('value').then(res => {
                    const data = [];
                    const keys = Object.keys(res.val());
                    const values = Object.values(res.val());
                    keys.map((key, index) => {
                        values[index].id = key;
                        data.push(values[index]);
                        return data;
                    })
                    setAudios(data);
                })
            }
            catch (err) {
                console.log(err);
            }
        };
        fetchData();
    }, [])
    const handleOnChangeSlider = ({ x }) => {
        audioRef.current.currentTime = x;
        setCurrentTime(x);
        if (!playAndPauseAudio) {
            setplayAndPauseAudio(true);
            audioRef.current.play();
        }
    }
    const onClickAudio = () => {
        if (!playAndPauseAudio) {
            audioRef.current.play();
        }
        else {
            audioRef.current.pause();
        }
        setplayAndPauseAudio(!playAndPauseAudio);
    }
    const handleOnLoadedData = () => {
        setDuration(audioRef.current.duration);
        if (playAndPauseAudio) {
            audioRef.current.play();
        }
    }
    const renderTime = () => {
        let html = null;
        let cMinute = Math.floor(currentTime / 60);
        let cSecond = Math.floor(currentTime % 60);
        let dMinute = Math.floor(duration / 60);
        let dSecond = Math.floor(duration % 60);
        let cTime = `${cMinute} : ${cSecond < 10 ? `0${cSecond}` : `${cSecond}`
            }`;
        let dTime = `${dMinute} : ${dSecond < 10 ? `0${dSecond}` : `${dSecond}`
            }`;
        html = `${cTime} / ${dTime}`;
        return html;
    }
    const selectIndex = (id) => {
        let index = findIndex(id, audios);
        if (index != -1) {
            setIndex(index);
            setActiveID(index);
        }
    }
    return (
        <div className="container-w">
            <div className="wrapper-audio">
                <img className={`audio-img ${playAndPauseAudio ? 'active' : ''}`} src={audios[index].image} alt={audios[0].name} />
                <p className="audio-name">{audios[index].name}</p>
                <p className="audio-artist">{audios[index].artist}</p>
                <audio
                    ref={audioRef}
                    src={audios[index].src}
                    onLoadedData={handleOnLoadedData}
                    onTimeUpdate={() => {
                        setCurrentTime(audioRef.current.currentTime)
                    }}
                ></audio>
                <p className="audio-time">
                    {renderTime()}
                </p>
                <div className="audio-controls">
                    <button className="audio-button" >
                        <IconContext.Provider value={{ size: 40 }}>
                            <BiSkipPreviousCircle />
                        </IconContext.Provider>
                    </button>
                    <button className="audio-button" onClick={onClickAudio}>
                        <IconContext.Provider value={{ size: 40 }}>
                            {playAndPauseAudio ? <AiOutlinePauseCircle /> : <AiOutlinePlayCircle />}
                        </IconContext.Provider>
                    </button>
                    <button className="audio-button" >
                        <IconContext.Provider value={{ size: 40 }}>
                            <BiSkipNextCircle />
                        </IconContext.Provider>
                    </button>
                </div>
                <TimeSlider
                    axis="x"
                    xmin={0}
                    x={currentTime}
                    xmax={duration}
                    onChange={handleOnChangeSlider}
                    styles={{
                        track: {
                            backgroundColor: "#e3e3e3",
                            height: "8px",
                            width: "400px"
                        },
                        active: {
                            backgroundColor: "black",
                            height: "8px",
                        },
                        thumb: {
                            width: "20px",
                            height: "20px",
                            backgroundColor: "#e183e3",
                            borderRadius: '50px',
                        },
                    }}
                />
            </div>
            <div className="wrapper-song">
                <IconContext.Provider value={{ size: 40, color: "#fffffff" }}>
                    <AiOutlineCloseCircle />
                </IconContext.Provider>
                <p className="title-song">List Audio</p>
                <AudioItems
                    active={activeID}
                    data={audios}
                    handleSaveIndex={selectIndex}
                />
            </div>
        </div>

    );
}

export default App;
